mapper.insert:
insert into house (id, message, senddate
)
values (#{house_id,jdbcType=VARCHAR}, #{message,jdbcType=VARCHAR}, #{senddate,jdbcType=TIMESTAMP}
)

mapper.update:
update house
set message = #{message,jdbcType=VARCHAR},
senddate = #{senddate,jdbcType=TIMESTAMP}
where house_id = #{id,jdbcType=VARCHAR}

mapper.select:
SELECT

FROM house


        AND house_hold_id = #{houseHoldId,jdbcType=INTEGER}


        AND house_hold_room_id = #{houseHoldRoomId,jdbcType=VARCHAR}


        AND house_hold_name = #{houseHoldName,jdbcType=VARCHAR}


        AND house_hold_phone = #{houseHoldPhone,jdbcType=INTEGER}


        AND house_hold_sex = #{houseHoldSex,jdbcType=VARCHAR}


        AND house_hold_individual = #{houseHoldIndividual,jdbcType=TIMESTAMP}


        AND house_hold_type = #{houseHoldType,jdbcType=TIMESTAMP}


        AND house_hold_description = #{houseHoldDescription,jdbcType=TIMESTAMP}


        AND house_hold_create_at = #{houseHoldCreateAt,jdbcType=TIMESTAMP}


        AND house_hold_update_at = #{houseHoldUpdateAt,jdbcType=TIMESTAMP}


order by house_hold_id DESC

SELECT COUNT(*) FROM house


        AND house_hold_id = #{houseHoldId,jdbcType=INTEGER}


        AND house_hold_room_id = #{houseHoldRoomId,jdbcType=VARCHAR}


        AND house_hold_name = #{houseHoldName,jdbcType=VARCHAR}


        AND house_hold_phone = #{houseHoldPhone,jdbcType=INTEGER}


        AND house_hold_sex = #{houseHoldSex,jdbcType=VARCHAR}


        AND house_hold_individual = #{houseHoldIndividual,jdbcType=TIMESTAMP}


        AND house_hold_type = #{houseHoldType,jdbcType=TIMESTAMP}


        AND house_hold_description = #{houseHoldDescription,jdbcType=TIMESTAMP}


        AND house_hold_create_at = #{houseHoldCreateAt,jdbcType=TIMESTAMP}


        AND house_hold_update_at = #{houseHoldUpdateAt,jdbcType=TIMESTAMP}
